import type { PoolOptions } from "mysql2/promise";
import fs from "fs";

export interface MysqlPoolConfig extends PoolOptions {
  uri?: string;
}

export function buildMysqlPoolConfig(connectionString: string): MysqlPoolConfig {
  const url = new URL(connectionString);

  const database = url.pathname.replace(/^\//, "");
  if (!database) {
    throw new Error("DATABASE_URL is missing the database name");
  }

  const sslCaPath = process.env.PGSSL_CA?.trim();
  let ssl: PoolOptions["ssl"] | undefined;

  if (sslCaPath) {
    ssl = {
      ca: fs.readFileSync(sslCaPath),
      minVersion: "TLSv1.2",
      rejectUnauthorized: true,
    };
  } else {
    ssl = {
      minVersion: "TLSv1.2",
      rejectUnauthorized: false,
    };
  }

  const port = url.port ? Number(url.port) : 3306;

  return {
    host: url.hostname,
    port,
    user: decodeURIComponent(url.username),
    password: decodeURIComponent(url.password),
    database,
    waitForConnections: true,
    connectionLimit: Number(process.env.DB_POOL_SIZE ?? 10),
    maxIdle: 5,
    idleTimeout: 60000,
    ssl,
  };
}

