        return 'bg-red-500/20 text-red-500 border-red-500/20';
      case 'warning':
        return 'bg-yellow-500/20 text-yellow-500 border-yellow-500/20';
      default:
        return 'bg-blue-500/20 text-blue-500 border-blue-500/20';
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <Bell className="w-8 h-8" />
            Notifications
          </h1>
          <p className="text-muted-foreground">Manage your notifications</p>
        </div>
        <div className="flex gap-2">
          {unreadCount > 0 && (
            <Button variant="outline" onClick={handleMarkAllAsRead}>
              <Check className="w-4 h-4 mr-2" />
              Mark All Read
            </Button>
          )}
          {notifications.length > 0 && (
            <Button variant="outline" onClick={handleClearAll}>
              <Trash2 className="w-4 h-4 mr-2" />
              Clear All
            </Button>
          )}
        </div>
      </div>

      {unreadCount > 0 && (
        <Card>
          <CardContent className="pt-6">
            <Badge variant="secondary" className="text-lg px-4 py-2">
              {unreadCount} unread notification{unreadCount !== 1 ? 's' : ''}
            </Badge>
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader>
          <CardTitle>All Notifications</CardTitle>
          <CardDescription>Your notification history</CardDescription>
        </CardHeader>
        <CardContent>
          {notifications.length === 0 ? (
            <div className="text-center py-12 text-muted-foreground">
              <Bell className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>No notifications</p>
            </div>
          ) : (
            <div className="space-y-2">
              {notifications.map((notification) => (
                <div
                  key={notification.id}
                  className={cn(
                    'flex items-start gap-4 p-4 rounded-lg border transition-colors',
                    !notification.read && 'bg-muted/50',
                    getTypeColor(notification.type)
                  )}
                >
                  <div
                    className={cn(
                      'flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center font-bold',
                      getTypeColor(notification.type)
                    )}
                  >
                    {getTypeIcon(notification.type)}
                  </div>
                  <div className="flex-1 space-y-1">
                    <div className="flex items-start justify-between">
                      <div>
                        <h3 className="font-semibold">{notification.title}</h3>
                        <p className="text-sm text-muted-foreground">{notification.message}</p>
                      </div>
                      {!notification.read && (
                        <Badge variant="secondary" className="ml-2">
                          New
                        </Badge>
                      )}
                    </div>
                    <p className="text-xs text-muted-foreground">
                      {format(new Date(notification.timestamp), 'MMM dd, yyyy HH:mm')}
                    </p>
                  </div>
                  <div className="flex gap-2">
                    {!notification.read && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleMarkAsRead(notification.id)}
                      >
                        <Check className="w-4 h-4" />
                      </Button>
                    )}
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleRemove(notification.id)}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

scripts/setup-database.ts.content:
import "dotenv/config";
import mysql from "mysql2/promise";
import { readFileSync } from "fs";
import { join } from "path";
import { buildMysqlPoolConfig } from "../server/mysql-config.ts";

async function setupDatabase() {
  console.log("=== Database Setup Script ===\n");

  const databaseUrl = process.env.DATABASE_URL;

  if (!databaseUrl || databaseUrl.trim() === "") {
    console.error("❌ DATABASE_URL is not set or is empty");
    process.exit(1);
  }

  console.log("✓ DATABASE_URL found");
  console.log(`  Length: ${databaseUrl.length} characters\n`);

  try {
    console.log("Connecting to TiDB/MySQL database...");
    const config = buildMysqlPoolConfig(databaseUrl);
    const pool = mysql.createPool(config);

    console.log("✓ Connection established\n");

    console.log("Reading SQL schema file...");
    const sqlPath = join(process.cwd(), "scripts", "create-tables.sql");
    const sql = readFileSync(sqlPath, "utf-8");

    console.log("Executing SQL statements...");
    // Remove comments and split by semicolons (TiDB Cloud doesn't allow multi-statement)
    const cleanedSql = sql
      .split("\n")
      .map((line) => {
        const commentIndex = line.indexOf("--");
        return commentIndex >= 0 ? line.substring(0, commentIndex) : line;
      })
      .join("\n");

    const statements = cleanedSql
      .split(";")
      .map((s) => s.trim().replace(/\s+/g, " "))
      .filter((s) => s.length > 0 && !s.toLowerCase().includes("information_schema"));

    console.log(`Executing ${statements.length} SQL statements...`);
    for (let i = 0; i < statements.length; i++) {
      const statement = statements[i];
      if (statement.length > 0) {
        try {
          const preview = statement.substring(0, 60).replace(/\s+/g, " ");
          console.log(`  [${i + 1}/${statements.length}] ${preview}...`);
          await pool.query(statement);
        } catch (error: any) {
          // Ignore "table already exists" and "index already exists" errors
          if (
            error.code === "ER_TABLE_EXISTS_ERROR" ||
            error.code === "ER_DUP_KEYNAME" ||
            error.code === "ER_DUP_ENTRY" ||
            error.message?.includes("already exists") ||
            error.code === "ER_NO_SUCH_TABLE" // Index creation might fail if table doesn't exist yet, but we'll retry
          ) {
            console.log(`    ⚠ Skipped: ${error.code || error.message}`);
            continue;
          }
          console.error(`    ❌ Error:`, error.message);
          throw error;
        }
      }
    }

    console.log("✓ All tables created successfully\n");

    const [rows] = await pool.query(`
      SELECT table_name 
      FROM information_schema.tables 
      WHERE table_schema = DATABASE()
      ORDER BY table_name
    `);

    console.log("\nCreated tables:");
    (rows as { table_name: string }[]).forEach((row) => {
      console.log(`  ✓ ${row.table_name}`);
    });

    await pool.end();
    console.log("\n✅ Database setup complete!");
  } catch (error) {
    console.error("\n❌ Error setting up database:");
    console.error(error);
    process.exit(1);
  }
}

setupDatabase();

